function changeCursorLabel(e){let r=e.target.getAttribute("cursor-label"),t=document.querySelector(".cursor-circle_label");t.textContent=r}function resetCursorLabel(){let e=document.querySelector(".cursor-circle_label");e.textContent=""}const hoverItems=document.querySelectorAll("[cursor-label]");hoverItems.forEach(e=>{e.addEventListener("mouseenter",changeCursorLabel),e.addEventListener("mouseleave",resetCursorLabel)});
